class ReturnPhase {
/**
 *   -  if **x** is bigger than **max** return `x` 
     -  if **max** is bigger than **x** return `max` 
     -  all other cases return `x`	

 */
	
	public static int max(int x, int max){
	   
		if (x > max) {
	        return x;
	    } else if (max > x) {
	        return max;
	    } else {
	        return x;
	    }
	}

        
	
	
}


